mapper.select:
( SELECT id AS postId, title, content, user_name AS userName, user_id AS userId, module_id AS moduleId, type, `status`, create_time AS createTime FROM post WHERE type = '1' and module_id = #{moduleId}
and user_id = #{userId}
and (title like #{keyWord} or content like #{keyWord})
and status = #{status}
and type = #{postType}
ORDER BY create_time ) UNION (SELECT id AS postId, title, content, user_name AS userName, user_id AS userId, module_id AS moduleId, type, `status`, create_time AS createTime FROM post WHERE type NOT IN ( '1' ) and module_id = #{moduleId}
and user_id = #{userId}
and (title like #{keyWord} or content like #{keyWord})
and status = #{status}
and type = #{postType}
ORDER BY create_time )

SELECT id AS relationId, title AS relationContent, `status`
FROM post
WHERE id = #{postId}

