mapper.select:
SELECT p.id AS postId, p.title, p.content, p.user_name AS userName, p.user_id AS userId, p.module_id AS moduleId, m.type AS moduleType, p.type, p.`status`, p.create_time AS createTime, c.commentNum AS commentNum, p.popular_index AS popularIndex, visit.visitNum AS visitNum FROM post p LEFT JOIN ( SELECT c.post_id, COUNT( 1 ) AS commentNum FROM `comment` c LEFT JOIN ( SELECT relation_id, COUNT( 1 ) AS replyNum FROM `comment` WHERE `status` = '0' AND relation_type = '1' GROUP BY relation_id ) r ON c.id = r.relation_id WHERE c.`status` = '0' GROUP BY c.post_id ) c ON p.id = c.post_id LEFT JOIN module m ON p.module_id = m.id LEFT JOIN ( SELECT post_id, COUNT( 1 ) AS visitNum FROM visit WHERE `status` = '0' GROUP BY post_id ) visit ON p.id = visit.post_id WHERE p.STATUS = '0' AND p.type = '3'
AND m.type = #{moduleType}
ORDER BY p.popular_index DESC, p.create_time DESC LIMIT 10
